worldBuildingWindow	= "background: rgb(240, 240, 240);\ncolor: rgb(0, 0, 0);"
worldBuildingLabel	= 'QLabel {\n	background: transparent;\n	font: font "Fira Code";\n}'
accept				= "QPushButton {\n    background: rgb(230, 230, 230);\n    color: rgb(0, 0, 0);\n}\n\nQPushButton:hover {\n	background: rgb(220, 220, 220);\n	color: rgb(0, 0, 0);\n}\n\nQPushButton:disabled {\n	background: rgb(240, 240, 240);\n	color: rgb(126, 126, 126);\n}"
cancel				= "QPushButton {\n    background: rgb(230, 230, 230);\n    color: rgb(0, 0, 0);\n}\n\nQPushButton:hover {\n	background: rgb(220, 220, 220);\n	color: rgb(0, 0, 0);\n}\n\nQPushButton:disabled {\n	background: rgb(240, 240, 240);\n	color: rgb(126, 126, 126);\n}"
textEditor			= "QTextEdit {\n	background: rgb(255, 255, 255);\n	border: 1px solid rgb(200, 200, 200);\n	border-radius:5px;\n	padding: 3px;\n}"